import React from "react";

/**
 * Image-based badge renderer with optional verification link
 */
export default function CredlyBadge({
  imageName,
  badgeName,
  verification,
  width = 140,
  height = 140,
}) {
  const imgSrc =
    imageName?.startsWith("http")
      ? imageName
      : `${process.env.PUBLIC_URL}/images/${imageName}`;

  return (
    <div style={{ textAlign: "center" }}>
      <a
        href={verification}
        target="_blank"
        rel="noopener noreferrer"
        title={`Verify ${badgeName}`}
      >
        <figure
          className="image credly-badge"
          style={{
            width: `${width}px`,
            height: `${height}px`,
            margin: "0 auto",
            display: "inline-block",
          }}
          aria-label={badgeName}
        >
          <img
            src={imgSrc}
            alt={badgeName}
            loading="lazy"
            style={{
              width: "100%",
              height: "100%",
              objectFit: "contain",
              display: "block",
            }}
          />
        </figure>
      </a>

      {verification && (
        <div className="cert-verification">
          <a
            href={verification}
            target="_blank"
            rel="noopener noreferrer"
          >
            Verify credential
          </a>
        </div>
      )}
    </div>
  );
}
